import React, { useState } from 'react';
import { Box, Button, Typography, TextField } from '@mui/material';
import { Editor, EditorState, RichUtils } from 'draft-js';
import 'draft-js/dist/Draft.css';

const Dashboard = () => {
  const [editorState, setEditorState] = useState(() => EditorState.createEmpty());
  const [pdfFile, setPdfFile] = useState(null);

  const handleKeyCommand = (command) => {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      setEditorState(newState);
      return 'handled';
    }
    return 'not-handled';
  };

  const handleFileChange = (e) => {
    setPdfFile(e.target.files[0]);
  };

  const handleConvertToWord = () => {
    if (pdfFile) {
      // Logic to convert PDF to Word (integration with backend API)
      console.log('Converting PDF to Word:', pdfFile.name);
    } else {
      alert('Please select a PDF file to convert.');
    }
  };
  return (
    <Box style={{ textAlign: 'center', padding: '50px' }}>
      <Typography variant="h4">PDF to Word Converter</Typography>
      <Box style={{ marginTop: '20px' }}>
        <input type="file" accept="application/pdf" onChange={handleFileChange} />
        <Button variant="contained" color="primary" onClick={handleConvertToWord} style={{ marginLeft: '20px' }}>
          Convert to Word
        </Button>
      </Box>
      <Box style={{ marginTop: '40px' }}>
        <Typography variant="h5">Note Editor</Typography>
        <div style={{ border: '1px solid #ccc', padding: '10px', marginTop: '10px' }}>
          <Editor editorState={editorState} handleKeyCommand={handleKeyCommand} onChange={setEditorState} />
        </div>
      </Box>
    </Box>
  );
};

export default Dashboard;
