import React, { useState } from "react";
import { Box, Button, Typography, TextField } from "@mui/material";
import { Editor, EditorState, RichUtils } from "draft-js";
import axios from "axios";
import "draft-js/dist/Draft.css";

const Dashboard = () => {
  const [loading, setLoading] = useState(false);
  const [editorState, setEditorState] = useState(() =>
    EditorState.createEmpty()
  );
  const [pdfFile, setPdfFile] = useState(null);

  const handleKeyCommand = (command) => {
    const newState = RichUtils.handleKeyCommand(editorState, command);
    if (newState) {
      setEditorState(newState);
      return "handled";
    }
    return "not-handled";
  };

  const handleFileChange = (e) => {
    setPdfFile(e.target.files[0]);
  };

  const handleConvertToWord = async () => {
    if (pdfFile) {
      const formData = new FormData();
      formData.append("file", pdfFile);

      setLoading(true);

      try {
        const response = await axios.post(
          "http://localhost:5001/convert",
          formData,
          {
            responseType: "blob", // Important to download the file
          }
        );

        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute(
          "download",
          `${pdfFile.name.replace(".pdf", "")}.docx`
        );
        document.body.appendChild(link);
        link.click();
      } catch (error) {
        console.error("Error converting PDF to Word:", error);
        alert("Conversion failed. Please try again.");
      } finally {
        setLoading(false);
      }
    } else {
      alert("Please select a PDF file to convert.");
    }
  };

  return (
    <Box style={{ textAlign: "center", padding: "50px" }}>
      <Typography variant="h4">PDF to Word Converter</Typography>
      <Box style={{ marginTop: "20px" }}>
        <input
          type="file"
          accept="application/pdf"
          onChange={handleFileChange}
        />
        <Button
          variant="contained"
          color="primary"
          onClick={handleConvertToWord}
          style={{ marginLeft: "20px" }}
          disabled={loading}
        >
          {loading ? "Converting..." : "Convert to Word"}
        </Button>
      </Box>
      <Box style={{ marginTop: "40px" }}>
        <Typography variant="h5">Note Editor</Typography>
        <div
          style={{
            border: "1px solid #ccc",
            padding: "10px",
            marginTop: "10px",
          }}
        >
          <Editor
            editorState={editorState}
            handleKeyCommand={handleKeyCommand}
            onChange={setEditorState}
          />
        </div>
      </Box>
    </Box>
  );
};

export default Dashboard;
